import './List.css';
import ListItem from '../listItem/ListItem';
import whatsNext from '../../resources/whats-next.png'



const List = ({task, selectTask,selectedItems}) => {

{tasks.map(item => (
	<div key={item.id}>
	  {editingId === item.id ? (
		<>
		  <input
			value={editedText}
			onChange={(e) => setEditedText(e.target.value)}
		  />
		  <button onClick={() => saveEdit(item.id)}> Сохранить</button>
		  <button onClick={() => cancelEdit()}> Отмена</button>
		</>
	  ) : (
		<>
		  <span>{item.task}</span>
		  <button onClick={() => startEdit(item.id, item.task)}>✏️ Редактировать</button>
		</>
	  )}
	</div>
))}

 

    const elem = task.map(i => <ListItem key={i.id} task={i} selectTask={selectTask}  selectedItems={selectedItems}/>)


    return(
        <>
        <img className='imgWhatsNext' src={whatsNext} alt="whatsNext" />
            <ul>
                {elem}
            </ul>
        </>

        
    )
}

export default List
