import { useState, useEffect } from 'react';
import { serverGetTasks, serverDeleteTask, serverAddTask, serverMarkTaskAsDone } from '../../services/task';

import Panel from '../panel/Panel';
import List from '../list/List';
import './App.css';

function App() {

  	const [task, setTask] = useState([])
  	const [selectedItems, setSelectedItems] = useState([])

	useEffect(()=>{
		serverGetTasks()
		.then(setTask)
	}, [])

  console.log(selectedItems)
  console.log(task)



    //const randomInteger =(min, max)=> {
    //    let rand = min + Math.random() * (max + 1 - min);
    //    return Math.floor(rand);
    //}

	const addTask = async (text) =>{
		if(text){		
			const newTask = await serverAddTask(text)			//захист від пустого інпуту
			setTask([...task, newTask])					//формування данних таски 
			//{id: randomInteger(0, 10000), text, done: false}
		}
  	}

	const selectTask = (id) =>{
		setSelectedItems(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]		//додавання в стейт id тих таск які обрані користувачем 

		)
	}

	const toogleStatus = async(id) => {
		await serverMarkTaskAsDone(id)
		setTask(prev => prev.map(task => selectedItems.includes(task.id) ? {...task, done: !task.done} : task));	//зміна поля done
		setSelectedItems([])
	}

	const deleteTask = async (id) => {
		await serverDeleteTask(id)
		setTask(prev => prev.filter(task => !selectedItems.includes(task.id)  ));	//фільтрація стейту з тасками по id, ті що обрані будуть відалені фільтрацією
		setSelectedItems([])
	}




 	 return (
    	<div className='container'>
        	<Panel addTask={addTask} toogleStatus={toogleStatus} deleteTask={deleteTask} selectedItems={selectedItems} />
        	<div className='App'>



        		<List task={task} selectTask={selectTask} selectedItems={selectedItems} />

    	</div>
    	</div>

  	);
}

export default App;
