import './List.css';
import ListItem from '../listItem/ListItem';
import whatsNext from '../../resources/whats-next.png'
import { useState } from 'react';



const List = ({task, selectTask,selectedItems, startEdit, saveEdit, editedId, editedText, cancelEdit, setEditedText}) => {



 

    const elem = task.map(i => 	(<div key={i.id}>
        {editedId === i.id ? (
          
          <div className='editingInputBlock'>
            <input className='editingInput'
              value={editedText}
              onChange={(e) => setEditedText(e.target.value)}
            />
            <div className="editBtnBlock">
                <button className='confirm' onClick={() => saveEdit(i.id)}> Сохранить</button>
                <button className='cancellation' onClick={() => cancelEdit()}> Отмена</button>
            </div>

          </div>

          
        ) : (
          <ListItem key={i.id} task={i} selectTask={selectTask}  selectedItems={selectedItems} startEdit={startEdit}/>
        )}
      </div>
  ))


    return(
        <>
        <img className='imgWhatsNext' src={whatsNext} alt="whatsNext" />
            <ul>
                {elem}
            </ul>
        </>

        
    )
}

export default List
