const ListItem = ({selectTask, task, selectedItems, i}) => {
    return(
        <li>
                    
            <h2 className='status' style={{background: selectedItems.includes(task.id) ?' #FFA07A' :' #00FF7F'}}>{task.done ? 'выполнено' : 'в работе'}</h2>
            <h2 className='task'>task: {task.text}</h2>
            <button onClick={()=>selectTask(task.id)}  className='chooseBtn'>choose</button>
    
        </li>
    )
}

export default ListItem
