import { useState } from 'react';

import Panel from '../panel/Panel';
import List from '../list/List';
import './App.css';

function App() {

  const [task, setTask] = useState([])
  const [selectedItems, setSelectedItems] = useState([])

  console.log(selectedItems)
  console.log(task)
    const randomInteger =(min, max)=> {
        let rand = min + Math.random() * (max + 1 - min);
        return Math.floor(rand);
    }

	const selectTask = (id) =>{
		setSelectedItems(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]		//додавання в стейт id тих таск які обрані користувачем 

		)
	}

	const toogleStatus = () => {
		setTask(prev => prev.map(task => selectedItems.includes(task.id) ? {...task, done: !task.done} : task));	//зміна поля done
		setSelectedItems([])
	}

	const deleteTask = () => {
		setTask(prev => prev.filter(task => !selectedItems.includes(task.id)  ));	//фільтрація стейту з тасками по id, ті що обрані будуть відалені фільтрацією
		setSelectedItems([])
	}


	const addTask = (text) =>{
		if(text){																	//захист від пустого інпуту
			setTask([...task, {id: randomInteger(0, 10000), text, done: false}])	//формування данних таски 
		}
  	}

 	 return (
    	<div className='container'>
        	<Panel addTask={addTask} toogleStatus={toogleStatus} deleteTask={deleteTask} />
        	<div className='App'>



        		<List task={task} selectTask={selectTask} selectedItems={selectedItems} />

    	</div>
    	</div>

  	);
}

export default App;
