import './List.css';
import ListItem from '../listItem/ListItem';
import whatsNext from '../../resources/whats-next.png'
import { useState } from 'react';



const List = ({task, selectTask,selectedItems, startEdit, saveEdit, editedId, editedText, cancelEdit}) => {

const [editText, setEditedText] = useState('')

 

    const elem = task.map(i => 	(<div key={i.id}>
        {editedId === i.id ? (
          <>
            <input
              value={editedText}
              onChange={(e) => setEditedText(e.target.value)}
            />
            <button onClick={() => saveEdit(i.id)}> Сохранить</button>
            <button onClick={() => cancelEdit()}> Отмена</button>
          </>
        ) : (
          <ListItem key={i.id} task={i} selectTask={selectTask}  selectedItems={selectedItems} startEdit={startEdit}/>
        )}
      </div>
  ))


    return(
        <>
        <img className='imgWhatsNext' src={whatsNext} alt="whatsNext" />
            <ul>
                {elem}
            </ul>
        </>

        
    )
}

export default List
