import { useState } from 'react';

import Panel from '../panel/Panel';
import List from '../list/List';
import './App.css';

function App() {

  const [task, setTask] = useState([])
  const [selectedItems, setSelectedItems] = useState([])

  console.log(selectedItems)
  console.log(task)
    const randomInteger =(min, max)=> {
        let rand = min + Math.random() * (max + 1 - min);
        return Math.floor(rand);
    }

	const selectTask = (id) =>{
		setSelectedItems(prev => prev.includes(id) ? prev.filter(i => i !== id) : [...prev, id]

		)
	}

	const toogleStatus = () => {
		setTask(prev => prev.map(task => selectTask.includes(task.id) ? {...task, done: !prev.done} : task))
		selectedItems([])
	}

	const deleteTask = () => {
		setTask(prev => prev.filter(task => !selectTask.includes(task.id)  ))
		setSelectedItems([])
	}


	const addTask = (text) =>{
		if(text){
			setTask([...task, {id: randomInteger(0, 10000), text, done: false}])
		}

  	}

 	 return (
    	<div className='container'>
        	<Panel addTask={addTask} toogleStatus={toogleStatus} deleteTask={deleteTask}/>
        	<div className='App'>



        		<List task={task} selectTask={selectTask}/>

    	</div>
    	</div>

  	);
}

export default App;
