const ListItem = ({selectTask, task, selectedItems}) => {

    const colorStatus =(data)=>{
            let color = ''
                if(selectedItems.includes(data)){
                    color = ' #FFA07A'
                }if(!selectedItems.includes(data)){
                    color = ' rgb(0, 255, 127)'
                }else{ color =' rgb(252, 232, 116)'}

                return color
                
    }


    return(
        <li>
                    
            <h2 className='status' style={{background: ()=> colorStatus(task.id)}} >
                {task.done ? 'выполнено' : 'в работе'}
            </h2>

            <h2 className='task'>task: {task.text}</h2>
            <button onClick={()=>selectTask(task.id)}  className='chooseBtn'>choose</button>
    
        </li>
    )
}

export default ListItem
